// Enhanced response formatting with comprehensive data processing
const aiResponse = $json.output;

// Enhanced AI response parsing with error handling
let responseData;
try {
  // Handle both string and object responses from AI
  responseData = typeof aiResponse === 'string' ? JSON.parse(aiResponse) : aiResponse;

  // Validate required response structure
  if (!responseData.message) {
    throw new Error('Missing required message field');
  }
} catch (error) {
  console.error('AI Response Parsing Error:', error);

  // Fallback response with error context
  responseData = {
    message: typeof aiResponse === 'string' ? aiResponse : "Thank you for your response. I'm processing your information.",
    error: {
      type: 'response_parsing_error',
      details: error.message,
      originalResponse: aiResponse
    }
  };
}

// Enhanced response validation and enrichment
const formattedResponse = {
  message: responseData.message || "Thank you for sharing your financial information.",
  formData: responseData.formData || null,
  currentStep: responseData.currentStep || null,
  nextStep: responseData.nextStep || null,
  progress: responseData.progress || {
    current: 1,
    total: 4,
    completed: []
  },
  extractedData: responseData.extractedData || null,
  recommendations: responseData.recommendations || [],

  // Enhanced metadata
  metadata: {
    timestamp: new Date().toISOString(),
    processingTime: Date.now() - ($('Process Onboarding Step').item.json.processingContext?.timestamp ? new Date($('Process Onboarding Step').item.json.processingContext.timestamp).getTime() : Date.now()),
    language: $('Process Onboarding Step').item.json.language || 'en',
    userId: $('Process Onboarding Step').item.json.userId,
    agentVersion: '2.0.0',
    responseQuality: responseData.extractedData ? 'high' : 'standard'
  },

  // Error handling
  ...(responseData.error && { error: responseData.error })
};

// Enhanced logging for monitoring and debugging
console.log('Enhanced Response Formatting:', {
  messageLength: formattedResponse.message.length,
  hasFormData: !!formattedResponse.formData,
  hasExtractedData: !!formattedResponse.extractedData,
  recommendationsCount: formattedResponse.recommendations.length,
  processingTime: formattedResponse.metadata.processingTime,
  responseQuality: formattedResponse.metadata.responseQuality
});

return [{
  json: formattedResponse
}];

