// Analyze financial data and determine alerts to generate
const userData = $input.all();
const alerts = [];
const now = new Date();

// Process each user's financial data
for (const user of userData) {
  const {
    user_id,
    user_email, 
    user_language,
    user_currency,
    budget_id,
    category_id,
    category_name,
    budget_amount,
    current_spent,
    budget_utilization_percent,
    transaction_count,
    last_transaction,
    avg_transaction,
    transactions_last_7_days,
    spending_last_7_days,
    avg_daily_spending
  } = user.json;

  // Skip if no budget data
  if (!budget_id) continue;

  // 1. Budget Overspend Alerts
  if (budget_utilization_percent >= 120) {
    alerts.push({
      user_id,
      user_email,
      user_language,
      user_currency,
      alert_type: 'budget_critical_overspend',
      severity: 'critical',
      category_name,
      category_id,
      budget_amount,
      current_spent,
      utilization_percent: budget_utilization_percent,
      title: user_language === 'vi' ? 
        `ðŸš¨ VÆ°á»£t ngÃ¢n sÃ¡ch ${category_name}` :
        `ðŸš¨ ${category_name} Budget Exceeded`,
      priority: 3
    });
  } else if (budget_utilization_percent >= 100) {
    alerts.push({
      user_id,
      user_email,
      user_language,
      user_currency,
      alert_type: 'budget_overspend',
      severity: 'warning',
      category_name,
      category_id,
      budget_amount,
      current_spent,
      utilization_percent: budget_utilization_percent,
      title: user_language === 'vi' ? 
        `âš ï¸ ÄÃ£ háº¿t ngÃ¢n sÃ¡ch ${category_name}` :
        `âš ï¸ ${category_name} Budget Used Up`,
      priority: 2
    });
  } else if (budget_utilization_percent >= 80) {
    alerts.push({
      user_id,
      user_email,
      user_language,
      user_currency,
      alert_type: 'budget_warning',
      severity: 'info',
      category_name,
      category_id,
      budget_amount,
      current_spent,
      utilization_percent: budget_utilization_percent,
      title: user_language === 'vi' ? 
        `ðŸ’¡ Sáº¯p háº¿t ngÃ¢n sÃ¡ch ${category_name}` :
        `ðŸ’¡ ${category_name} Budget Nearly Used`,
      priority: 1
    });
  }

  // 2. Unusual Spending Pattern Alerts
  if (avg_daily_spending && avg_transaction) {
    const unusual_threshold = avg_transaction * 3; // 3x average transaction
    const recent_high_spending = spending_last_7_days > (avg_daily_spending * 14); // 2x weekly average

    if (recent_high_spending) {
      alerts.push({
        user_id,
        user_email,
        user_language,
        user_currency,
        alert_type: 'unusual_spending_pattern',
        severity: 'warning',
        category_name,
        category_id,
        spending_last_7_days,
        avg_daily_spending,
        title: user_language === 'vi' ? 
          `ðŸ“ˆ Chi tiÃªu báº¥t thÆ°á»ng cho ${category_name}` :
          `ðŸ“ˆ Unusual Spending in ${category_name}`,
        priority: 2
      });
    }
  }

  // 3. Inactive Spending Alerts (no transactions in frequently used categories)
  if (transaction_count > 5 && last_transaction) { // Only for active categories
    const daysSinceLastTransaction = Math.floor((now - new Date(last_transaction)) / (1000 * 60 * 60 * 24));

    if (daysSinceLastTransaction >= 14) {
      alerts.push({
        user_id,
        user_email,
        user_language,
        user_currency,
        alert_type: 'spending_inactive',
        severity: 'info',
        category_name,
        category_id,
        days_since_last: daysSinceLastTransaction,
        title: user_language === 'vi' ? 
          `ðŸ¤” KhÃ´ng cÃ³ giao dá»‹ch ${category_name} gáº§n Ä‘Ã¢y` :
          `ðŸ¤” No Recent ${category_name} Transactions`,
        priority: 1
      });
    }
  }
}

// Return alerts grouped by user for efficient processing
const alertsByUser = alerts.reduce((acc, alert) => {
  if (!acc[alert.user_id]) {
    acc[alert.user_id] = [];
  }
  acc[alert.user_id].push(alert);
  return acc;
}, {});

const result = Object.entries(alertsByUser).map(([user_id, userAlerts]) => ({
  user_id,
  alerts: userAlerts,
  alert_count: userAlerts.length,
  highest_priority: Math.max(...userAlerts.map(a => a.priority)),
  user_email: userAlerts[0].user_email,
  user_language: userAlerts[0].user_language,
  user_currency: userAlerts[0].user_currency
}));

return result.length > 0 ? result : [{ no_alerts: true, message: 'No alerts generated for this batch' }];

// Process alerts for database insertion and notification
const userAlerts = $json.alerts;
const processed = [];

for (const alert of userAlerts) {
  // Format currency amounts
  const formatAmount = (amount, currency) => {
    if (currency === 'VND') {
      return new Intl.NumberFormat('vi-VN').format(amount) + ' VND';
    } else {
      return new Intl.NumberFormat('en-US', { 
        style: 'currency', 
        currency: currency || 'USD' 
      }).format(amount);
    }
  };

  // Generate detailed alert message based on type and language
  let message = '';
  let actionUrl = '';

  const { alert_type, user_language, category_name, current_spent, budget_amount, utilization_percent, user_currency } = alert;

  switch (alert_type) {
    case 'budget_critical_overspend':
      if (user_language === 'vi') {
        message = `Báº¡n Ä‘Ã£ chi ${formatAmount(current_spent, user_currency)} cho ${category_name}, vÆ°á»£t ${utilization_percent}% ngÃ¢n sÃ¡ch ${formatAmount(budget_amount, user_currency)}. HÃ£y xem xÃ©t Ä‘iá»u chá»‰nh chi tiÃªu!`;
      } else {
        message = `You've spent ${formatAmount(current_spent, user_currency)} on ${category_name}, exceeding ${utilization_percent}% of your ${formatAmount(budget_amount, user_currency)} budget. Consider adjusting your spending!`;
      }
      actionUrl = `/dashboard?tab=budget&category=${alert.category_id}`;
      break;

    case 'budget_overspend':
      if (user_language === 'vi') {
        message = `NgÃ¢n sÃ¡ch ${category_name} Ä‘Ã£ háº¿t! Báº¡n Ä‘Ã£ chi ${formatAmount(current_spent, user_currency)} trong thÃ¡ng nÃ y.`;
      } else {
        message = `Your ${category_name} budget is used up! You've spent ${formatAmount(current_spent, user_currency)} this month.`;
      }
      actionUrl = `/dashboard?tab=budget&category=${alert.category_id}`;
      break;

    case 'budget_warning':
      const remaining = budget_amount - current_spent;
      if (user_language === 'vi') {
        message = `Báº¡n Ä‘Ã£ sá»­ dá»¥ng ${utilization_percent}% ngÃ¢n sÃ¡ch ${category_name}. CÃ²n láº¡i ${formatAmount(remaining, user_currency)} cho thÃ¡ng nÃ y.`;
      } else {
        message = `You've used ${utilization_percent}% of your ${category_name} budget. ${formatAmount(remaining, user_currency)} remaining this month.`;
      }
      actionUrl = `/dashboard?tab=budget&category=${alert.category_id}`;
      break;

    case 'unusual_spending_pattern':
      if (user_language === 'vi') {
        message = `Chi tiÃªu ${category_name} tuáº§n nÃ y cao hÆ¡n bÃ¬nh thÆ°á»ng. HÃ£y kiá»ƒm tra cÃ¡c giao dá»‹ch gáº§n Ä‘Ã¢y.`;
      } else {
        message = `Your ${category_name} spending this week is higher than usual. Check your recent transactions.`;
      }
      actionUrl = `/dashboard?tab=transactions&category=${alert.category_id}`;
      break;

    case 'spending_inactive':
      if (user_language === 'vi') {
        message = `KhÃ´ng cÃ³ giao dá»‹ch ${category_name} trong ${alert.days_since_last} ngÃ y. CÃ³ váº¥n Ä‘á» gÃ¬ khÃ´ng?`;
      } else {
        message = `No ${category_name} transactions for ${alert.days_since_last} days. Is everything okay?`;
      }
      actionUrl = `/dashboard?tab=transactions&category=${alert.category_id}`;
      break;
  }

  // Prepare database record
  processed.push({
    user_id: alert.user_id,
    alert_type: alert.alert_type,
    title: alert.title,
    message: message,
    severity: alert.severity,
    action_url: actionUrl,
    data: {
      category_id: alert.category_id,
      category_name: alert.category_name,
      budget_amount: alert.budget_amount,
      current_spent: alert.current_spent,
      utilization_percent: alert.utilization_percent,
      priority: alert.priority,
      currency: alert.user_currency
    },
    expires_at: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString(), // 7 days
    // Notification data
    notification_title: alert.title,
    notification_body: message,
    user_email: alert.user_email,
    user_language: alert.user_language
  });
}

return processed;

// Prepare FCM notification payload
const alertData = $('Process Alerts for Storage').item.json;
const userData = $input.first().json;
const tokens = JSON.parse(userData.fcm_tokens);

const notifications = [];

// Create notification for each FCM token
for (const token of tokens) {
  if (token && typeof token === 'string') {
    notifications.push({
      token: token,
      notification: {
        title: alertData.notification_title,
        body: alertData.notification_body,
        icon: 'ic_notification',
        sound: 'default'
      },
      data: {
        alert_type: alertData.alert_type,
        severity: alertData.severity,
        action_url: alertData.action_url,
        category_id: alertData.data.category_id || '',
        alert_id: $('Insert Alert Record').first().json.id || '',
        timestamp: new Date().toISOString()
      },
      android: {
        notification: {
          channelId: 'financial_alerts',
          priority: alertData.severity === 'critical' ? 'high' : 'default',
          defaultSound: true,
          defaultVibrateTimings: true
        }
      },
      apns: {
        payload: {
          aps: {
            alert: {
              title: alertData.notification_title,
              body: alertData.notification_body
            },
            sound: 'default',
            badge: 1
          }
        }
      }
    });
  }
}

return notifications;

// Log notification delivery result
const fcmResponse = $input.first().json;
const alertData = $('Process Alerts for Storage').item.json;
const notificationData = $('Prepare FCM Notifications').item.json;

const deliveryResult = {
  alert_id: $('Insert Alert Record').first().json.id,
  user_id: alertData.user_id,
  alert_type: alertData.alert_type,
  notification_sent: !!fcmResponse.name,
  fcm_message_id: fcmResponse.name || null,
  delivery_status: fcmResponse.name ? 'sent' : 'failed',
  error: fcmResponse.error || null,
  timestamp: new Date().toISOString(),
  token_used: notificationData.token
};

return [deliveryResult];

// Log when no FCM tokens are available
const alertData = $('Process Alerts for Storage').item.json;

return [{
  alert_id: $('Insert Alert Record').first().json.id,
  user_id: alertData.user_id,
  alert_type: alertData.alert_type,
  notification_sent: false,
  delivery_status: 'no_tokens',
  error: 'No FCM tokens available for user',
  timestamp: new Date().toISOString()
}];

// Log skipped duplicate alert
const alertData = $('Process Alerts for Storage').item.json;

return [{
  user_id: alertData.user_id,
  alert_type: alertData.alert_type,
  status: 'skipped_duplicate',
  message: 'Alert skipped - similar alert sent within 24 hours',
  timestamp: new Date().toISOString()
}];

// Log when no alerts are generated for the batch
const batchInfo = $input.first().json;

return [{
  batch_status: 'no_alerts',
  message: batchInfo.message || 'No alerts generated for this user batch',
  timestamp: new Date().toISOString(),
  execution_id: $('Init Monitoring Context').first().json.execution_id
}];
